import React, { useState, useEffect } from 'react';
import axios from 'axios';

const QuoteDisplay = () => {
  const [quoteData, setQuoteData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [quote, setQuote] = useState('');
  const [author, setAuthor] = useState('');

  useEffect(() => {
      fetchRandomQuote();
    }, []); // Fetch the random quote when the component mounts

  const fetchRandomQuote = () => {
    setLoading(true); // Set loading to true when fetching data
    // Fetch data from the backend server
    axios.get('http://localhost:5050/quote')
      .then((response) => {
        setQuoteData(response.data);
        setLoading(false);  // Set loading to false when data is received
      })
      .catch((error) => {
        console.error('Error fetching quote:', error);
        setLoading(false); // Set loading to false even on error to handle the case where no quotes are available
      });
  };

  const getRandomQuote = () => {
      if (quoteData.length === 0) return null;
      const randomIndex = Math.floor(Math.random() * quoteData.length);
      return quoteData[randomIndex];
  };

  const randomQuote = getRandomQuote();

  return (
    <div>
      <h2>Random Quote</h2>
      {loading ? (
        <p>Loading...</p> // Show loading message while fetching data
      ) : quoteData.length === 0 || !randomQuote ? (
        <p>No quotes found.</p> // Show no quotes message if there are no quotes
      ) : (
        <>
          <p>{randomQuote.quote}</p>
          <p>- {randomQuote.author}</p>
          <button onClick={fetchRandomQuote}>Get Another Quote</button> {/* Button to get a new random quote */}
        </>
      )}
    </div>
  );
};

export default QuoteDisplay;
