import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ThirdPartyQuoteDisplay = () => {
  const [loading, setLoading] = useState(true);
  const [randomQuote, setRandomQuote] = useState(null);
  const [firstQuoteFetched, setFirstQuoteFetched] = useState(false);

  useEffect(() => {
    fetchRandomQuote();
  }, []);

  const fetchRandomQuote = () => {
    setLoading(true);
    axios
      .get('https://api.quotable.io/random')
      .then((response) => {
        setRandomQuote(response.data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching quote:', error);
        setLoading(false);
      });
  };

  const handleGetRandomQuote = () => {
    if (firstQuoteFetched) {
      fetchRandomQuote();
    } else {
      fetchRandomQuote();
      setFirstQuoteFetched(true);
    }
  };

  return (
    <div>
      <h3>Random Quote</h3>
      {loading ? (
        <p>Loading...</p>
      ) : randomQuote && randomQuote.content ? (
        <>
          <h4 className='Quote'>{randomQuote.content}</h4>
          <p className='Author'>- {randomQuote.author}</p>
          <button className="GetQuoteButton" onClick={handleGetRandomQuote}>
            {firstQuoteFetched ? 'Get Another Quote' : 'Get Quote'}
          </button>
        </>
      ) : (
        <p>No quotes found.</p>
      )}
    </div>
  );
};

export default ThirdPartyQuoteDisplay;
