import './App.css';
import React from 'react';

function App() {
  const [data, setData] = React.useState({content:arguments,author:arguments,tags:arguments});

  function getQuote() {
    try{
    fetch('https://api.quotable.io/random').then(
      response => response.json()).then(
        (quote)=>{
          setData(quote);
        }
      )
    }
    catch(error){
      console.log(error);
    }
  }


  return (
    <div className="App">
      <header className="App-header">
      <h1 className='Title'>Random Quote Generator</h1>
      <h3 className='Quote'>Quote: &lt;Random Quote displays here &gt; {data.content}</h3>       
      <h5 className='Author'>Author: &lt;Author of Quote displays here &gt; {data.author}</h5>
      <h5 className='Author'>Tag: &lt;Tags of Quote displays here &gt; {data.tags}</h5>
      <button className='Generator' onClick={getQuote}>Get Quote</button>
      </header>
    </div>
  );
}

export default App;
